puzzle_len   = len( puzzle_input )
up_cnt       = 0
for x in puzzle_input:
  if x == "(":
    up_cnt += 1

print("Part 1:")
print( up_cnt - (puzzle_len - up_cnt) )

# PART 2
# Now, given the same instructions, find the position of the first character that causes him 
# to enter the basement (floor -1). The first character in the instructions has position 1, 
# the second character has position 2, and so on.
floor = 0
for idx, val in enumerate(puzzle_input):
  floor -= -1 if val == "(" else 1 # ternary
  if floor < 0:
    print(f'Char at {idx+1} placed us in the basement first')
    break
